const cc = client.channels.find(channel => channel.name === msg.author.id);
if(!cc) {
const guild = client.guilds.get("SERVER ID HERE");
const category = guild.channels.find(channel => channel.id == "TICKET CATEGORY ID HERE" && channel.type == "category");
guild.createChannel(msg.author.id, "text")
.then(channel => {
    if(!category) throw new Error("Category does not exist!");
    channel.setParent(category.id).then(channel => {
        channel.send("**" + msg.author.tag + "**" + " has made a new ticket: " + msg.content).then(() => msg.author.send("**Thank you for your message. It will be reviewed by a moderator at their earliest convience.**")).then(() => client.channels.find(channel => channel.name == msg.author.id).lockPermissions());
    });
}).catch(console.error);
}
else {
    cc.send("**" + msg.author.tag + "**" + ": " + msg.content)
}

if(msg.attachments.size > 0 && msg.channel.type == "text") {
    const u = client.users.find(user => user.id === msg.channel.name);
    msg.attachments.forEach(function(attachment) {
        u.send(attachment.url);
    });
}

else if(msg.attachments.size > 0 && msg.channel.type == "dm") {
    const channelToGet = client.channels.find(channel => channel.name == msg.author.id)
    msg.attachments.forEach(function(attachment) {
        channelToGet.send(attachment.url);
    });
}

else {
    console.log("No attachments")
}
