const cc = client.channels.find(channel => channel.name === msg.author.id);
if(!cc) {
const guild = client.guilds.get("SERVER ID HERE");
const category = guild.channels.find(channel => channel.id == "TICKET CATEGORY ID HERE" && channel.type == "category");
guild.createChannel(msg.author.id, "text")
.then(channel => {
    if(!category) throw new Error("Category does not exist!");
    channel.setParent(category.id).then(channel => {
        channel.send("**" + msg.author.tag + "**" + " has made a new ticket: " + msg.content).then(() => msg.author.send("Staff have recieved your ticket."))
    });
}).catch(console.error);
}
else {
    cc.send("**" + msg.author.tag + "**" + ": " + msg.content)
}
